INSERT INTO categories (id, name, description) VALUES  (1, 'Roses', 'Classic and elegant roses for every occasion.'), (2, 'Lilies', 'Beautiful lilies in various colors and types.'), (3, 'Exotics', 'Unique and rare flowers for a distinct touch.'), (4, 'Bouquets', 'Arrangements for special moments.');

INSERT INTO products (id, name, description, description_long, price) VALUES  (101, 'Red Rose Dozen', 'A classic bouquet of twelve vibrant red roses, symbolizing love and passion.', 'Twelve long-stemmed, deep red roses carefully arranged with lush greenery and delicate baby\'s breath. Perfect for anniversaries, Valentine\'s Day, or expressing heartfelt emotions. Each rose is hand-picked for its freshness and vibrant color, ensuring a breathtaking display.', 450.00), (102, 'White Lily Bunch', 'Elegant white lilies, known for their pure beauty and fragrant aroma.', 'A stunning collection of pristine white lilies with large, captivating blooms. Their gentle fragrance creates a serene atmosphere, making them ideal for sympathy, congratulations, or adding a touch of sophistication to any home. Each stem boasts multiple blossoms, offering a full and luxurious appearance.', 35.50), (103, 'Orchid Pot', 'A delicate and long-lasting phalaenopsis orchid in a decorative pot.', 'A beautiful potted phalaenopsis orchid, known for its graceful arching stems and long-lasting blooms. This low-maintenance plant adds an exotic touch to any interior space, perfect for gifting or enhancing your home decor. Available in various soft pastel colors, it brings serene beauty and a touch of the tropics.', 550.00), (104, 'Sunflower Joy', 'Bright and cheerful sunflowers, perfect for bringing warmth to any day.', 'A vibrant bouquet featuring large, sunny sunflowers that radiate happiness and warmth. Accented with seasonal fillers and greenery, this arrangement is perfect for cheering someone up, celebrating new beginnings, or simply brightening a room. Their bold presence makes a strong statement.', 28.75), (105, 'Mixed Spring Bouquet', 'A delightful mix of seasonal spring flowers, fresh and colorful.', 'An enchanting assortment of fresh spring blooms, including tulips, daffodils, hyacinths, and irises. This colorful bouquet captures the essence of spring, bringing joy and a sense of renewal. Each flower is hand-selected for its beauty and freshness, creating a harmonious and uplifting arrangement for any occasion.', 520.00), (106, 'Purple Hyacinth Pot', 'Fragrant purple hyacinths, a symbol of sincerity and peace.', 'A charming pot of richly colored purple hyacinths, filling the air with their sweet, intoxicating fragrance. These beautiful spring bulbs symbolize peace and sincerity, making them a thoughtful gift. Their dense, bell-shaped clusters create an appealing visual and aromatic presence.', 220.00), (107, 'Bird of Paradise Stem', 'Exotic and striking Bird of Paradise flowers, a truly unique statement.', 'A single, vibrant Bird of Paradise stem, renowned for its distinctive, crane-like shape and brilliant orange and blue hues. This exotic flower brings a tropical flair and architectural interest to any floral arrangement, perfect for modern decor or a bold statement. Long-lasting and captivating.', 180.00), (108, 'Peony Dream', 'Lush, full peonies, a symbol of prosperity and good fortune.', 'A magnificent cluster of voluminous peonies, celebrated for their large, ruffled petals and enchanting fragrance. These opulent blooms come in shades of pink, white, and coral, symbolizing prosperity and honor. Perfect for weddings, grand celebrations, or adding a touch of luxurious romance to your home.', 650.00), (109, 'Gerbera Daisy Mix', 'Cheerful and long-lasting gerbera daisies in a spectrum of colors.', 'A vibrant assortment of colorful gerbera daisies, known for their large, cheerful blooms and long vase life. These happy flowers are perfect for birthdays, get-well wishes, or simply to brighten the day with their joyful presence. Their sturdy stems make them easy to arrange.', 29.50), (110, 'Lavender Bunch', 'A fragrant bundle of dried lavender, perfect for relaxation and decor.', 'A rustic and aromatic bundle of dried lavender, exuding a calming and soothing fragrance. Ideal for aromatherapy, crafting, or adding a touch of rustic charm to your home decor. Known for its relaxing properties, itâ€™s perfect for bedrooms or living spaces.', 150.00);

